INSERT INTO `letras` VALUES (1,1,'Doce presença, És meu abrigo<br> Meu protetor, Em Ti confio<br>',1),(1,2,'Mesmo sem ver, eu posso crer<br>Que Tua promessa está de pé<br>Irá se cumprir!<br>',1),(1,3,'<u class=\"cdx-underline\">Quem sou eu pra questionar um Deus fiel?<br></u><u class=\"cdx-underline\">Tu és Deus e Tua palavra está de pé!<br></u>',1),(1,4,'És minha&nbsp;<u class=\"cdx-underline\">âncora,</u>&nbsp;A minha&nbsp;<u class=\"cdx-underline\">esperança!<br></u><u class=\"cdx-underline\">Em que minha fé firmada está!<br>Eu não temerei,</u>&nbsp;<u class=\"cdx-underline\">eu não temerei!</u><br><b>[2x]</b>',1),(1,5,'<mark class=\"cdx-marker\"><u class=\"cdx-underline\">Volta no começo e repete novamente.</u></mark>',1),(1,6,'<u class=\"cdx-underline\">Eu sei que no vale também estás!</u><br><u class=\"cdx-underline\">E sobre o bravo mar me ensina a caminhar!</u><br><u class=\"cdx-underline\">Tu és o quarto homem na fornalha!</u><br><u class=\"cdx-underline\">Tu és o meu Deus que não falha!</u><br><b>[2x]</b>',1),(1,7,'És minha&nbsp;<u class=\"cdx-underline\">âncora,</u>&nbsp;A minha&nbsp;<u class=\"cdx-underline\">esperança!<br></u><u class=\"cdx-underline\">Em que minha fé firmada está!<br>Eu não temerei,</u>&nbsp;<u class=\"cdx-underline\">eu não temerei!</u><br><b>[2x]</b>',1),(2,1,'<mark class=\"cdx-marker\">Homens:</mark>O sacrifício vivo Se entregou por mim!<br>Com armas de amor Ele lutou até o fim!<br>Vestido de silêncio, o semblante era de dor! <br>A todos que O feriu lá no Calvário perdoou!<br>',1),(2,2,'<mark class=\"cdx-marker\">Meninas:</mark>Subiu naquela cruz sem nada reclamar!<br>Cordeiro imaculado que sofreu por me amar!<br>E a minha vida foi lavada pelo sangue de Jesus!<br>Minha história dividiu, antes e depois!<br>Pelo sangue, Pelo sangue de Jesus!<br>',1),(2,3,'<mark class=\"cdx-marker\">Todos:</mark>Fui salvo&nbsp;pelo sangue!&nbsp;Fui curado pelo sangue!<br>Fui liberto&nbsp;pelo sangue!&nbsp;Fui comprado pelo sangue!<br>O sangue de&nbsp;Jesus que me leva até o céu!<br>O sangue de&nbsp;Jesus, vermelho,&nbsp;forte e fiel!<br>Suportou a cruz a&nbsp;preço de sangue por amor!<br>Precioso&nbsp;sangue de Jesus!',1),(2,4,'<mark class=\"cdx-marker\">Homens:</mark>O sacrifício vivo eu quero ser pra Deus!<br>Tão puro e agradável como Cristo ofereceu!<br>Com a Sua verdade vou me comprometer!<br>E ser exemplo vivo de Sua graça e poder!<br>',1),(2,5,'<mark class=\"cdx-marker\">Meninas:</mark>Amar como a mim mesmo a quem me odiar!<br>Usar as minhas mãos pra outras mãos abençoar!<br><mark class=\"cdx-marker\">Todos:</mark>E já não vivo eu, mas agora Jesus Cristo vive em mim!<br>O meu passado apagou! Tudo perdoou!<br>Pelo sangue, Pelo sangue de Jesus!',1),(2,6,'Fui salvo pelo sangue!&nbsp;Fui curado pelo sangue!<br>Fui liberto pelo sangue!&nbsp;Fui comprado pelo sangue!<br>O sangue de Jesus que me leva até o céu!<br>O sangue de Jesus, vermelho, forte e fiel!<br>Suportou a cruz a&nbsp;preço de sangue por amor!<br>Precioso&nbsp;sangue de Jesus!<br><b>[2x]</b>',1),(3,1,'<mark class=\"cdx-marker\">Todos:</mark> <u class=\"cdx-underline\">Glória,&nbsp;Rompendo em Glória<br> De Glória em Glória, Reflete a Glória</u>',1),(3,2,'<u class=\"cdx-underline\">A Igreja vem com aqu’Ele que brilha mais que a luz do sol<br>Dissipando as trevas, destruindo o mal<br>A Sua face é um  refletor de Glória,<br>O céu derrama a Glória porque a Igreja vem,<br>Arrebentando com as portas do inferno<br>Com barulho de milagre, levantando um exército de vidas</u><br><mark class=\"cdx-marker\">Solo:&nbsp;</mark>É a Igreja Santa, aqui perseguida...',1),(3,3,'Tentaram acabar&nbsp;com a base da Igreja<br> Sem ela a Terra&nbsp;não povoaria o céu<br>Ela não teria força pra adorar,<br>Não haveria missionário em todo lugar<br>Mas o plano&nbsp;não deu certo... não, não!<br>',1),(3,4,'Tentaram nos calar matando alguns irmãos,<br>Mas nada adiantou surgiram mais cristãos!<br><mark class=\"cdx-marker\">Todos:</mark><u class=\"cdx-underline\">Louvando como Paul